
  !(function ($) {
    "use strict";

    // Sidebar toggle behavior
    $("#sidebarToggle, #sidebarToggleTop").on("click", function (e) {
      $("body").toggleClass("sidebar-toggled");
      $(".sidebar").toggleClass("toggled");
      if ($(".sidebar").hasClass("toggled")) {
        $(".sidebar .collapse").collapse("hide");
      }
    });

    // Close sidebar collapse on window resize
    $(window).resize(function () {
      if ($(window).width() < 768) {
        $(".sidebar .collapse").collapse("hide");
      }
    });

    // Prevent sidebar scrolling on hover when fixed-nav is used
    $("body.fixed-nav .sidebar").on("mousewheel DOMMouseScroll wheel", function (e) {
      if ($(window).width() > 768) {
        var e0 = e.originalEvent,
          delta = e0.wheelDelta || -e0.detail;
        this.scrollTop += 30 * (delta < 0 ? 1 : -1);
        e.preventDefault();
      }
    });

    // Scroll to top button appear
    $(document).on("scroll", function () {
      if ($(this).scrollTop() > 100) {
        $(".scroll-to-top").fadeIn();
      } else {
        $(".scroll-to-top").fadeOut();
      }
    });

    // Smooth scrolling when scroll-to-top is clicked
    $(document).on("click", "a.scroll-to-top", function (e) {
      var $anchor = $(this);
      $("html, body")
        .stop()
        .animate(
          { scrollTop: $($anchor.attr("href")).offset().top },
          1000,
          "easeInOutExpo"
        );
      e.preventDefault();
    });
  })(jQuery);

  // Modal triggers
  $(document).ready(function () {
    $("#myBtn").click(function () {
      $(".modal").modal("show");
    });
    $("#modalLong").click(function () {
      $(".modal").modal("show");
    });
    $("#modalScroll").click(function () {
      $(".modal").modal("show");
    });
    $("#modalCenter").click(function () {
      $(".modal").modal("show");
    });

    // Popovers
    $('[data-toggle="popover"]').popover();
    $(".popover-dismiss").popover({ trigger: "focus" });
  });

